import { ArrowBackIosNew, Edit } from "@mui/icons-material";
import {
  Button,
  Container,
  Divider,
  Grid,
  IconButton,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import MUIRichTextEditor from "mui-rte";
import React from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";

const RSPHCMB = () => {
  const { data } = useSelector((state) => state.rsphcmb);
  const history = useHistory();

  return (
    <div>
      <Container>
        <Button
          onClick={() => history.goBack()}
          startIcon={<ArrowBackIosNew />}
        >
          Back
        </Button>
        <br />
        <br />
        <Typography variant="h6" gutterBottom>
          WHO WE ARE
        </Typography>
        <Divider />
        <Grid container spacing={2}>
          <Grid item sm={6} md={5}>
            <Box display="flex" flexDirection="column">
              <img src={data?.image} alt="" />
              <Button
                sx={{ mt: 1, textTransform: "none" }}
                variant="contained"
                onClick={() =>
                  history.push({
                    pathname: "/dashboard/about/rsphcmb/who_we_are_image",
                    state: {
                      image: data?.image,
                    },
                  })
                }
              >
                Update Image
              </Button>
              <br />
            </Box>
          </Grid>

          <Grid item sm={6} md={7}>
            <Box display="flex" flexDirection="column">
              <Box
                display="flex"
                flexDirection="row"
                justifyContent="space-between"
                alignItems="center"
              >
                <Typography gutterBottom>CONTENT</Typography>
                <IconButton
                  onClick={() =>
                    history.push({
                      pathname: "/dashboard/about/rsphcmb/who_we_are_content",
                      state: {
                        content: data?.who_we_are,
                      },
                    })
                  }
                >
                  <Edit />
                </IconButton>
              </Box>
              <MUIRichTextEditor
                readOnly
                inlineToolbar={false}
                defaultValue={data?.who_we_are}
                toolbar={false}
              />
              <br />
            </Box>
          </Grid>
        </Grid>
        <br />
        <Divider />
        <br />

        <Box
          display="flex"
          flexDirection="row"
          justifyContent="space-between"
          alignItems="center"
        >
          <Typography variant="h6" gutterBottom>
            VISION & MISSION STATEMENT
          </Typography>
          <IconButton
            onClick={() =>
              history.push({
                pathname: "/dashboard/about/rsphcmb/vision_mission_edit",
                state: {
                  vision: data?.vision,
                  mission: data?.mission,
                  image: data?.visionImage,
                },
              })
            }
          >
            <Edit />
          </IconButton>
        </Box>
        <Divider />
        <Grid container spacing={2}>
          <Grid item sm={6} md={7}>
            <Box display="flex" flexDirection="column">
              <Typography variant="h6" gutterBottom>
                VISION STATEMENT
              </Typography>
              <br />
            </Box>
          </Grid>

          <Grid item sm={6} md={5}>
            <Box display="flex" flexDirection="column">
              <img src={data?.image} alt="" />
              <Button
                sx={{ mt: 1, textTransform: "none" }}
                variant="contained"
                onClick={() =>
                  history.push({
                    pathname: "/dashboard/about/rsphcmb/who_we_are_image",
                    state: {
                      image: data?.image,
                    },
                  })
                }
              >
                Update Image
              </Button>
              <br />
            </Box>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default RSPHCMB;
