import { Add, ArrowBackIosNew, Edit } from "@mui/icons-material";
import {
  Button,
  Container,
  Divider,
  Grid,
  IconButton,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import MUIRichTextEditor from "mui-rte";
import React from "react";
import { useSelector } from "react-redux";
import { useHistory, useLocation } from "react-router-dom";
import CommitteeTable from "../../../../../components/misc/table/comm_table";

const LGAItem = () => {
  const history = useHistory();
  const location = useLocation();

  const { LGAsData } = useSelector((state) => state.lgas);
  const [lgaFacilities, setLGAFacilities] = React.useState();
  const [lgaItem, setLGAItem] = React.useState();

  const [expanded, setExpanded] = React.useState(false);

  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };

  React.useEffect(() => {
    let faci = LGAsData?.filter((it) => it?.lga === location.state?.title);
    setLGAFacilities(faci[0]?.facilities);
    setLGAItem(faci[0]);
    // console.log("Facility: ", faci);
  }, [LGAsData]);

  return (
    <div>
      <Container>
        <Button
          onClick={() => history.goBack()}
          startIcon={<ArrowBackIosNew />}
        >
          Back
        </Button>
        <br />
        <br />

        <Box
          display={"flex"}
          flexDirection="row"
          justifyContent={"end"}
          alignItems="center"
        >
          <Button startIcon={<Edit />}>Edit</Button>
        </Box>
        <Divider />
        <br />
        <Grid container spacing={2}>
          <Grid item sm={6} md={5}>
            <Box display="flex" flexDirection="column">
              {/* <img src={data?.image} alt="" /> */}
              <Button
                sx={{ mt: 1, textTransform: "none" }}
                variant="contained"
                // onClick={() =>
                //   history.push({
                //     pathname: `/dashboard/about/lga/${location?.state?.title}/edit`,
                //     state: {
                //       title: location?.state?.title,
                //     },
                //   })
                // }
              >
                Update Image
              </Button>
              <br />
            </Box>
          </Grid>

          <Grid item sm={6} md={7}>
            <Box display="flex" flexDirection="column">
              <Typography gutterBottom>BACKGROUND INFORMATION</Typography>
              <Typography gutterBottom>{lgaItem?.bg_info}</Typography>
              <br />
              <br />
              <Typography gutterBottom>PHC TECHNICAL COMMITTEES</Typography>
              <Typography gutterBottom>{lgaItem?.comm_desc}</Typography>
              <br />
            </Box>
          </Grid>
        </Grid>

        <br />
        <br />
        <br />
        <Box
          display={"flex"}
          flexDirection="row"
          justifyContent={"space-between"}
          alignItems="center"
        >
          <Typography gutterBottom>COMMITTEES</Typography>
          <Button variant="contained" startIcon={<Add />}>
            Add New
          </Button>
        </Box>
        <Divider />
        <br />
        <Box>
          <CommitteeTable list={lgaItem?.committees} />
        </Box>

        <br />
        <br />
        <br />
        <Box
          display={"flex"}
          flexDirection="row"
          justifyContent={"space-between"}
          alignItems="center"
        >
          <Typography gutterBottom>FACILITIES</Typography>
          <Button variant="contained" startIcon={<Add />}>
            Add New
          </Button>
        </Box>
        <Divider />
        <br />
        <Box>
          <CommitteeTable list={lgaItem?.facilities} />
        </Box>
      </Container>
    </div>
  );
};

export default LGAItem;
