import { Add } from "@mui/icons-material";
import { Button, Card, CardMedia, Container, Typography } from "@mui/material";
import { Box } from "@mui/system";
import React from "react";

const ItemCard = (props) => {
  let { item } = props;

  return (
    <div>
      <Card>
        <Box
          display={"flex"}
          flexDirection="column"
          justifyContent={"start"}
          alignItems="stretch"
        >
          <CardMedia image={item?.image} />
          <div>
            <Typography gutterBottom={true}>{item?.title}</Typography>
            <Typography>{item?.description}</Typography>
          </div>
        </Box>
      </Card>
    </div>
  );
};

const Banner = () => {
  return (
    <div>
      <Container>
        <Box
          display={"flex"}
          flexDirection="row"
          justifyContent={"space-between"}
          alignItems="center"
        >
          <Typography variant="h5">Home Banner Slides</Typography>
          <Button
            startIcon={<Add />}
            variant="contained"
            sx={{ textTransform: "none" }}
          >
            Add Slide
          </Button>
        </Box>
      </Container>
    </div>
  );
};

export default Banner;
