import { ArrowBackIosNew, Edit } from "@mui/icons-material";
import {
  Button,
  Container,
  Divider,
  Grid,
  IconButton,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import MUIRichTextEditor from "mui-rte";

const PermSec = () => {
  const { permSecData } = useSelector((state) => state.permSec);
  const history = useHistory();

  return (
    <div>
      <Container>
        <Button
          onClick={() => history.goBack()}
          startIcon={<ArrowBackIosNew />}
        >
          Back
        </Button>
        <br />
        <br />
        <Grid container spacing={2}>
          <Grid item sm={6} md={5}>
            <Box display="flex" flexDirection="column">
              <img src={permSecData?.image} alt="" />
              <Button sx={{ mt: 1, textTransform: "none" }} variant="contained">
                Update Image
              </Button>
              <br />
              <Box
                paddingY={1}
                display={"flex"}
                flexDirection="row"
                justifyContent={"space-between"}
                alignItems="center"
              >
                <Typography>{permSecData?.name}</Typography>
                <Button
                  variant="contained"
                  sx={{ textTransform: "none" }}
                  startIcon={<Edit />}
                >
                  Update
                </Button>
              </Box>
              <Divider />
              <Box
                paddingY={1}
                display={"flex"}
                flexDirection="row"
                justifyContent={"space-between"}
                alignItems="center"
              >
                <Typography>{permSecData?.position}</Typography>
                <Button
                  variant="contained"
                  sx={{ textTransform: "none" }}
                  startIcon={<Edit />}
                >
                  Update
                </Button>
              </Box>
            </Box>
          </Grid>

          <Grid item sm={6} md={7}>
            <Box display="flex" flexDirection="column">
              <Typography variant="h6">BIOGRAPHY</Typography>
              <Divider />
              <MUIRichTextEditor
                readOnly
                inlineToolbar={false}
                style={{ width: "100%", textAlign: "center" }}
                toolbar={false}
                defaultValue={permSecData?.biography}
              />
            </Box>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default PermSec;
