  return (
    <div>
      <Container
        sx={{
          paddingX: 4,
        }}
      >
        <Button
          startIcon={<ArrowBackIosNew />}
          onClick={() => history.goBack()}
        >
          Back
        </Button>
        <Grid container spacing={2}>
          {lgaList?.map((elem, index) => (
            <Grid
              item
              xs={6}
              sm={6}
              md={4}
              key={index}
              display="flex"
              flexDirection="column"
              justifyContent="start"
            >
              <Typography
                variant="h6"
                fontWeight="700"
                paddingTop={2}
              >{`${elem.alphabet}`}</Typography>
              {elem?.item?.map((el, i) => (
                <Link
                  key={i}
                  to={{
                    pathname: "/dashboard/about/lga/" + el.name,
                    state: { title: el.name },
                  }}
                  style={{ textDecoration: "none" }}
                >{`${el.name}`}</Link>
              ))}
            </Grid>
          ))}
        </Grid>
      </Container>
    </div>
  );
};

export default LGAs;
