import {
  Avatar,
  Backdrop,
  Button,
  CircularProgress,
  Grid,
  Typography,
} from "@mui/material";
import React from "react";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import { makeStyles } from "@mui/styles";
import { useTheme } from "@mui/material";
import { useMediaQuery } from "@mui/material";
import { Box } from "@mui/system";
import {
  db,
  doc,
  ref,
  storage,
  uploadBytesResumable,
  updateDoc,
  getDownloadURL,
} from "../../../../../data/firebase";

const useStyles = makeStyles((theme) => ({
  root: {},
  divRoot: {
    display: "flex",
    padding: theme.spacing(3),
    flexDirection: "column",
    alignItems: "center",
  },
  avatarContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    borderColor: "#3EB3CE",
    borderWidth: 3,
    borderStyle: "solid",
  },
  avatar: {
    backgroundColor: theme.palette.secondary.main,
  },
  buttonRow: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
  },
  button: {
    margin: theme.spacing(1),
  },
  progress: {
    color: "white",
    zIndex: 500,
    margin: "auto",
  },
}));

const CircularProgressWithLabel = (props) => {
  return (
    <Box position="relative" display="inline-flex">
      <CircularProgress
        variant="determinate"
        {...props}
        size={90}
        thickness={3.0}
        style={{ color: "green" }}
      />
      <Box
        top={0}
        left={0}
        bottom={0}
        right={0}
        position="absolute"
        display="flex"
        alignItems="center"
        justifyContent="center"
      >
        <Typography
          variant="body1"
          component="div"
          style={{ color: "white", fontFamily: "roboto" }}
        >{`${Math.round(props.value)}%`}</Typography>
      </Box>
    </Box>
  );
};

const Account = () => {
  const classes = useStyles();
  const { userData } = useSelector((state) => state.user);
  const [imageFile, setImageFile] = React.useState(null);
  const [imageUrl, setImageUrl] = React.useState("");
  const [isUploading, setIsUploading] = React.useState(false);
  const [isLoading, setLoading] = React.useState(false);
  const [progress, setProgress] = React.useState(0);
  const fileInputRef = React.useRef();
  const { enqueueSnackbar } = useSnackbar();

  const initials =
    userData?.firstname?.slice(0, 1).toUpperCase() +
    userData?.lastname?.slice(0, 1).toUpperCase();

  let imgSize;
  const theme = useTheme();
  const xsmall = useMediaQuery(theme.breakpoints.only("xs"));
  const small = useMediaQuery(theme.breakpoints.only("sm"));
  const medium = useMediaQuery(theme.breakpoints.only("md"));
  const others = useMediaQuery(theme.breakpoints.up("lg"));

  if (xsmall) {
    imgSize = 150;
  } else if (small) {
    imgSize = 174;
  } else if (medium) {
    imgSize = 200;
  } else if (others) {
    imgSize = 236;
  }

  const handleFile = (e) => {
    setImageFile(e.target.files[0]);
    setImageUrl(URL.createObjectURL(e.target.files[0]));
  };

  const performSubmit = (e) => {
    setIsUploading(true);
    const timeNow = new Date();
    //First upload image to firebase storage then save to firestore
    const storageRef = ref(
      storage,
      "users-" + userData?.id + "/" + timeNow.getTime()
    );
    const uploadTask = uploadBytesResumable(storageRef, imageFile);

    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const uprogress =
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        // console.log("Upload is " + progress + "% done");
        setProgress(uprogress);
      },
      (error) => {
        // Handle unsuccessful uploads
        setIsUploading(false);
        // console.log(error);
        enqueueSnackbar(`${error?.message}`, { variant: "error" });
      },
      () => {
        setIsUploading(false);
        setLoading(true);
        getDownloadURL(uploadTask.snapshot.ref).then(async (downloadURL) => {
          const mRef = doc(db, "users", "" + userData?.id);
          try {
            await updateDoc(mRef, {
              image: downloadURL,
            });
            setLoading(false);
            enqueueSnackbar(`Image updated successfully`, {
              variant: "success",
            });
          } catch (error) {
            setLoading(false);
            enqueueSnackbar(
              `${error?.message || "Check your internet connection"}`,
              {
                variant: "error",
              }
            );
          }
        });
      }
    );
  };

  return (
    <div>
      <Backdrop style={{ zIndex: 1200 }} open={isUploading || isLoading}>
        {isUploading ? <CircularProgressWithLabel value={progress} /> : <div />}
        {isLoading ? (
          <CircularProgress
            size={90}
            thickness={3.0}
            style={{ color: "white" }}
          />
        ) : (
          <div />
        )}
      </Backdrop>
      <Grid container spacing={4}>
        <Grid item sm={6} md={6}>
          <div className={classes.divRoot}>
            <div
              className={classes.avatarContainer}
              style={{
                width: imgSize + 3,
                height: imgSize + 3,
                borderRadius: (imgSize + 3) / 2,
              }}
            >
              <Avatar
                src={
                  imageUrl
                    ? imageUrl
                    : userData?.image !== ""
                    ? userData?.image
                    : ""
                }
                style={{
                  width: imgSize - 12,
                  height: imgSize - 12,
                  borderRadius: (imgSize - 12) / 2,
                  fontSize: 64,
                }}
              >
                {userData?.image !== "" ? "" : initials}
              </Avatar>
            </div>
            <br />
            <input
              hidden={true}
              ref={fileInputRef}
              type="file"
              onChange={handleFile}
            />

            <div className={classes.buttonRow}>
              <Button
                className={classes.button}
                variant="contained"
                color="secondary"
                onClick={() => fileInputRef.current.click()}
              >
                Change Profile
              </Button>

              <Button
                hidden={imageFile ? false : true}
                disabled={isLoading || isUploading}
                className={classes.button}
                variant="contained"
                color="primary"
                onClick={performSubmit}
              >
                Save
              </Button>
            </div>
          </div>
        </Grid>
        <Grid item sm={6} md={6}></Grid>
      </Grid>
    </div>
  );
};

export default Account;
