import React from "react";
import { Drawer as MUIDrawer } from "@mui/material";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import Button from "@mui/material/Button";
import Divider from "@mui/material/Divider";
import PersonOutlineIcon from "@mui/icons-material/PersonOutline";
import DashBoardIcon from "@mui/icons-material/DashboardOutlined";
import PeopleOutlinedIcon from "@mui/icons-material/PeopleOutlined";
import SchoolOutlinedIcon from "@mui/icons-material/SchoolOutlined";
import { useHistory } from "react-router-dom";
import { makeStyles, useTheme } from "@mui/styles";
import useMediaQuery from "@mui/material/useMediaQuery";
import PowerSettingsNewIcon from "@mui/icons-material/PowerSettingsNew";
import logo from "../../../../assets/images/logo_white.svg";

import { useSnackbar } from "notistack";
import Skeleton from "@mui/material/Skeleton";

import { auth } from "../../../../data/firebase";
import { useDispatch } from "react-redux";
import { setUserData } from "../../../../data/redux/slice/user";

// import pattern from "../../../../assets/images/pattern.png";

const drawerWidth = 270;
const useStyles = makeStyles((theme) => ({
  drawer: {
    width: "275px",
    [theme.breakpoints.up("sm")]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  drawerPaper: {
    width: drawerWidth,
    backgroundColor: "#18113C",
  },
  toolbar: theme.mixins.toolbar,
  listRoot: {
    width: "100%",
    padding: theme.spacing(1),
  },
}));

const Drawer2 = (props) => {
  const classes = useStyles();
  const history = useHistory();
  const [selectedIndex, setSelectedIndex] = React.useState(0);
  // const { userStatus, userData } = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();

  const container =
    props.window !== undefined ? () => window().document.body : undefined;

  const drawerItems = [
    {
      text: "Overview",
      icon: (
        <DashBoardIcon
          style={selectedIndex === 0 ? { color: "white" } : { color: "black" }}
        />
      ),
      to: "/dashboard/",
    },
    {
      text: "Home",
      icon: (
        <SchoolOutlinedIcon
          style={selectedIndex === 1 ? { color: "white" } : { color: "black" }}
        />
      ),
      to: "/dashboard/home",
    },
    {
      text: "About",
      icon: (
        <PeopleOutlinedIcon
          style={selectedIndex === 2 ? { color: "white" } : { color: "black" }}
        />
      ),
      to: "/dashboard/about",
    },
    {
      text: "Services",
      icon: (
        <PersonOutlineIcon
          style={
            selectedIndex === 3 ? { color: "#4C3992" } : { color: "black" }
          }
        />
      ),
      to: "/dashboard/services",
    },
    {
      text: "Resources",
      icon: (
        <PersonOutlineIcon
          style={
            selectedIndex === 3 ? { color: "#4C3992" } : { color: "black" }
          }
        />
      ),
      to: "/dashboard/resources",
    },
    {
      text: "Contact",
      icon: (
        <PersonOutlineIcon
          style={
            selectedIndex === 3 ? { color: "#4C3992" } : { color: "black" }
          }
        />
      ),
      to: "/dashboard/contact",
    },
    {
      text: "Others",
      icon: (
        <PersonOutlineIcon
          style={
            selectedIndex === 3 ? { color: "#4C3992" } : { color: "black" }
          }
        />
      ),
      to: "/dashboard/others",
    },
    {
      text: "Users",
      icon: (
        <PersonOutlineIcon
          style={
            selectedIndex === 3 ? { color: "#4C3992" } : { color: "black" }
          }
        />
      ),
      to: "/dashboard/users",
    },
    {
      text: "Enquiries",
      icon: (
        <PersonOutlineIcon
          style={
            selectedIndex === 3 ? { color: "#4C3992" } : { color: "black" }
          }
        />
      ),
      to: "/dashboard/enquiries",
    },
  ];

  let picSize, cornerSize;
  const theme = useTheme();
  const xs = useMediaQuery(theme.breakpoints.only("xs"));
  const sm = useMediaQuery(theme.breakpoints.only("sm"));
  const md = useMediaQuery(theme.breakpoints.only("md"));
  if (xs) {
    cornerSize = 76;
    picSize = 70;
  } else if (sm) {
    cornerSize = 96;
    picSize = 90;
  } else if (md) {
    cornerSize = 116;
    picSize = 110;
  } else {
    cornerSize = 136;
    picSize = 130;
  }

  const handleListItemClick = (to, index) => {
    history.push(to);
    setSelectedIndex(index);
  };

  const signOut = async () => {
    props.handleBackdrop(true);
    // try {
    //   await auth.signOut();
    //   dispatch(setUserData(null));
    //   props.handleBackdrop(false);
    //   // enqueueSnackbar(`Successfully logged out`, { variant: "success" });
    //   // history.replace({
    //   //   pathname: "/login",
    //   // });
    // } catch (err) {
    //   enqueueSnackbar(`${err?.message || "Check your internet connection."}`, {
    //     variant: "error",
    //   });
    // }
    // finally {
    //   history.go(0);
    // }
  };

  const myDrawer = (
    <div
      style={{
        height: "100%",
        display: "flex",
        flexDirection: "column",
        // backgroundImage: "url(" + pattern + ")",
        // backgroundRepeat: "no-repeat",
        // backgroundSize: "cover",
        // backgroundPosition: "center",
        zIndex: 100,
      }}
    >
      <br />
      <div
        className={classes.toolbar}
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <img
          src={logo}
          style={{ width: "75%", margin: "auto" }}
          alt="site logo"
        />
      </div>
      <Divider />
      <br />
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          color: "white",
        }}
      >
        <List className={classes.listRoot}>
          {drawerItems.map((item, index) => {
            const { text, icon, to } = item;
            if (4 > 2) {
              return (
                <ListItem
                  sx={{
                    backgroundColor:
                      selectedIndex === index ? "white" : "#4C3992",
                  }}
                  button
                  key={index}
                  selected={selectedIndex === index}
                  onClick={() => handleListItemClick(to, index)}
                >
                  <ListItemIcon>{icon}</ListItemIcon>
                  <ListItemText primary={text} />
                </ListItem>
              );
            } else {
              return (
                <Skeleton
                  key={index}
                  component="li"
                  variant="rect"
                  animation="wave"
                  height={30}
                  style={{ margin: 10 }}
                />
              );
            }
          })}
        </List>
      </div>

      <div
        style={{
          flexDirection: "column",
          marginTop: "auto",
          marginRight: "auto",
          justifyContent: "left",
          alignItems: "start",
          padding: 16,
          zIndex: 1000,
        }}
      >
        <Button
          variant="text"
          color="secondary"
          startIcon={<PowerSettingsNewIcon />}
          style={{ textTransform: "none", zIndex: 1000 }}
          onClick={signOut}
        >
          Sign Out
        </Button>
      </div>
    </div>
  );

  return (
    <MUIDrawer
      variant="permanent"
      open
      classes={{ paper: classes.drawerPaper }}
    >
      {myDrawer}
    </MUIDrawer>
  );
};

export default Drawer2;
