import { Add, ArrowBackIosNew } from "@mui/icons-material";
import { Avatar, Button, Container, Divider, Typography } from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import { useHistory, useLocation } from "react-router-dom";
import CustomDialog from "../../../../../components/dialogs/custom-dialog";
import AddFunctionForm from "../../../../../forms/department/add_function";

const DepartmentInfo = () => {
  const history = useHistory();
  const location = useLocation();

  const [open, setOpen] = React.useState(false);

  return (
    <div>
      <CustomDialog
        open={open}
        bodyComponent={
          <AddFunctionForm
            id={location?.state?.id}
            list={location?.state?.functions || []}
            setOpen={setOpen}
          />
        }
      />
      <Container>
        <Box
          py={2}
          display="flex"
          flexDirction="row"
          justifyContent={"space-between"}
          alignItems="center"
        >
          <Button
            startIcon={<ArrowBackIosNew />}
            variant="text"
            onClick={() => history.goBack()}
          >
            Back
          </Button>
        </Box>
        <Typography variant="h5" gutterBottom={true}>
          {location.state?.title}{" "}
        </Typography>
        <Typography gutterBottom={true}>
          {location.state?.description}{" "}
        </Typography>
        <br />
        <Divider />
        <br />
        <Box
          display={"flex"}
          flexDirection="row"
          justifyContent={"space-between"}
          alignItems="center"
        >
          <Typography variant="h6">Funtions of Department</Typography>
          <Button
            variant="contained"
            startIcon={<Add />}
            onClick={() => setOpen(true)}
          >
            Add Function
          </Button>
        </Box>
        <Typography hidden={(location?.state?.functions || [])?.length < 1}>
          Below are the roles/functions of the department
        </Typography>
        {(location?.state?.functions || [])?.map((item, index) => (
          <Container key={index}>
            <Box
              display={"flex"}
              flexDirection="row"
              justifyContent={"space-between"}
              alignItems="center"
            >
              <div
                style={{
                  width: 6,
                  height: 6,
                  borderRadius: 3,
                  padding: 4,
                  backgroundColor: "black",
                }}
              />
              <Typography flex={1}>{item?.text}</Typography>
            </Box>
          </Container>
        ))}
      </Container>
    </div>
  );
};

export default DepartmentInfo;
