import { Add, ArrowBackIosNew } from "@mui/icons-material";
import { Avatar, Button, Container, Divider, Typography } from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import { useHistory, useLocation } from "react-router-dom";
import CustomDialog from "../../../../../components/dialogs/custom-dialog";
import AddFunctionForm from "../../../../../forms/department/add_function";
import { onSnapshot, doc, db } from "../../../../../../data/firebase";

const DepartmentInfo = () => {
  const history = useHistory();
  const location = useLocation();

  const [open, setOpen] = React.useState(false);
  const [functions, setFunctions] = React.useState([]);

  React.useEffect(() => {
    onSnapshot(doc(db, "departments", "" + location?.state?.id), (doc) => {
      // console.log("Current data: ", doc.data());
      setFunctions(doc.data()?.functions);
    });
  }, []);

  return (
    <div>
      <CustomDialog
        open={open}
        title="Add Department Function"
        handleClose={() => setOpen(false)}
        bodyComponent={
          <AddFunctionForm
            id={location?.state?.id}
            list={location?.state?.functions}
            setOpen={setOpen}
          />
        }
      />
      <Container>
        <Box
          py={2}
          display="flex"
          flexDirction="row"
          justifyContent={"space-between"}
          alignItems="center"
        >
          <Button
            startIcon={<ArrowBackIosNew />}
            variant="text"
            onClick={() => history.goBack()}
          >
            Back
          </Button>
        </Box>
        <Typography variant="h5" gutterBottom={true}>
          {location.state?.title}{" "}
        </Typography>
        <Typography gutterBottom={true}>
          {location.state?.description}{" "}
        </Typography>
        <br />
        <Divider />
        <br />
        <Box
          display={"flex"}
          flexDirection="row"
          justifyContent={"space-between"}
          alignItems="center"
        >
          <Typography variant="h6">Functions of Department</Typography>
          <Button
            variant="contained"
            startIcon={<Add />}
            onClick={() => setOpen(true)}
          >
            Add Function
          </Button>
        </Box>
        <Typography
          gutterBottom
          hidden={(location?.state?.functions || functions)?.length < 1}
        >
          Below are the roles/functions of the department
        </Typography>
        <br />
        {functions?.map((item, index) => (
          <Container key={index}>
            <Box
              display={"flex"}
              flexDirection="row"
              justifyContent={"space-between"}
              alignItems="start"
            >
              <Avatar color="red" />
              <Typography pl={2} flex={1}>
                {item?.text}
              </Typography>
            </Box>
          </Container>
        ))}
        {(location?.state?.functions || [])?.length < 1 && (
          <Box
            padding={10}
            display="flex"
            flexDirection="row"
            justifyContent="center"
            alignItems={"center"}
          >
            <Typography>No record found</Typography>
          </Box>
        )}
      </Container>
    </div>
  );
};

export default DepartmentInfo;
