import { ArrowBackIosNew, Edit } from "@mui/icons-material";
import {
  Button,
  Container,
  Divider,
  Grid,
  IconButton,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import MUIRichTextEditor from "mui-rte";
import React from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import CustomDialog from "../../../../components/dialogs/custom-dialog";
import CustomDialogImage from "../../../../components/dialogs/custom-dialog";
import WhoWeAreContent from "../../../../forms/about-rsphcmb/who_we_are_content";
import WhoWeAreImage from "../../../../forms/about-rsphcmb/who_we_are_img";

const RSPHCMB = () => {
  const { data } = useSelector((state) => state.rsphcmb);
  const history = useHistory();

  //   const [openImage, setOpenImage] = React.useState(false);
  const [openContent, setOpenContent] = React.useState(false);

  return (
    <div>
      {/* <CustomDialog
        title="Update Content"
        open={openContent}
        handleClose={setOpenContent(false)}
        bodyComponent={
          <WhoWeAreContent setOpen={setOpenContent} content={null} />
        }
      /> */}
      <Container>
        <Button
          onClick={() => history.goBack()}
          startIcon={<ArrowBackIosNew />}
        >
          Back
        </Button>
        <br />
        <br />
        <Typography variant="h6" gutterBottom>
          WHO WE ARE
        </Typography>
        <Divider />
        <Grid container spacing={2}>
          <Grid item sm={6} md={5}>
            <Box display="flex" flexDirection="column">
              <img src={data?.image} alt="" />
              <Button sx={{ mt: 1, textTransform: "none" }} variant="contained">
                Update Image
              </Button>
              <br />
            </Box>
          </Grid>

          <Grid item sm={6} md={7}>
            <Box display="flex" flexDirection="column">
              <Box
                display="flex"
                flexDirection="row"
                justifyContent="space-between"
                alignItems="center"
              >
                <Typography gutterBottom>CONTENT</Typography>
                <IconButton
                  onClick={() =>
                    history.push({
                      pathname: "/dashboard/about/rsphcmb/who_we_are_content",
                      state: {
                        content: data?.who_we_are,
                      },
                    })
                  }
                >
                  <Edit />
                </IconButton>
              </Box>
              <MUIRichTextEditor
                readOnly
                inlineToolbar={false}
                style={{ width: "100%", textAlign: "center" }}
                defaultValue={permSecData?.message}
                toolbar={false}
              />
              <Typography>{data?.who_we_are}</Typography>
              <br />
            </Box>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default RSPHCMB;
