import { Edit } from "@mui/icons-material";
import { Button, Container, Divider, Grid, Typography } from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import { useSelector } from "react-redux";

const PermSec = () => {
  const { permSecData } = useSelector((state) => state.permSec);

  return (
    <div>
      <Container>
        <Grid container spacing={2}>
          <Grid item sm={6} md={5}>
            <Box display="flex" flexDirection="column">
              <img src={permSecData?.image} alt="" />
              <Button sx={{ mt: 1, textTransform: "none" }} variant="contained">
                Update Image
              </Button>
              <br />
              <Box
                paddingY={1}
                display={"flex"}
                flexDirection="row"
                justifyContent={"space-between"}
                alignItems="center"
              >
                <Typography>{permSecData?.name}</Typography>
                <Button
                  variant="contained"
                  sx={{ textTransform: "none" }}
                  startIcon={<Edit />}
                >
                  Update
                </Button>
              </Box>
              <Divider />
              <Box
                paddingY={1}
                display={"flex"}
                flexDirection="row"
                justifyContent={"space-between"}
                alignItems="center"
              >
                <Typography>{permSecData?.position}</Typography>
                <Button
                  variant="contained"
                  sx={{ textTransform: "none" }}
                  startIcon={<Edit />}
                >
                  Update
                </Button>
              </Box>
            </Box>
          </Grid>

          <Grid item sm={6} md={7}>
            <Box display="flex" flexDirection="column">
              <Typography>Message from permanent secretary</Typography>
              <br />
              <Divider />
              <br />
              <Typography variant="h6">BIOGRAPHY</Typography>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default PermSec;
