const UpdatePassword = (props) => {
  let { oldPassword, setOpen, userId } = props;
  const [formValues, setFormValues] = React.useState({
    oldPassword: "",
    newPassword: "",
  });
  const [showCode, setShowCode] = React.useState(false);
  const [showCode2, setShowCode2] = React.useState(false);
  const [isLoading, setLoading] = React.useState(false);
  const [isError, setError] = React.useState(false);

  const { enqueueSnackbar } = useSnackbar();
  const history = useHistory();
  const dispatch = useDispatch();

  const user = auth.currentUser;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues((prevData) => ({ ...prevData, [name]: value }));
    if (oldPassword !== formValues.oldPassword) {
      setError(true);
    } else {
      setError(false);
    }
  };

  const updateData = (e) => {
    setLoading(true);

    if (formValues.oldPassword === oldPassword) {
      setError(false);
      updatePassword(user, formValues.newPassword)
        .then(async () => {
          // Update successful.
          try {
            const mRef = doc(db, "users", "" + userId);
            await updateDoc(mRef, {
              password: formValues.newPassword,
            });
            setOpen(false);
            setLoading(false);
            await auth.signOut();
            dispatch(setMyData(null));
            enqueueSnackbar(`Successfully logged out`, { variant: "success" });
            history.replace({
              pathname: "/",
            });
            enqueueSnackbar(`Password updated successfully`, {
              variant: "success",
            });
          } catch (error) {
            setLoading(false);
            enqueueSnackbar(
              `${error?.message || "Check your internet connection"}`,
              {
                variant: "error",
              }
            );
          }
        })
        .catch((error) => {
          // An error ocurred
          setLoading(false);
          enqueueSnackbar(
            `${error?.message || "Check your internet connection"}`,
            {
              variant: "error",
            }
          );
        });
    } else {
      setError(true);
      setLoading(false);
      enqueueSnackbar(`${"Incorrect Old Password!"}`, {
        variant: "error",
      });
    }
  };

  return (
    <div>
      <Backdrop style={{ zIndex: 1200 }} open={isLoading}>
        {isLoading ? (
          <CircularProgress
            size={90}
            thickness={3.0}
            style={{ color: "white" }}
          />
        ) : (
          <div />
        )}
      </Backdrop>
      <ValidatorForm onSubmit={updateData}>
        <TextValidator
          margin="normal"
          required
          fullWidth
          name="oldPassword"
          label="Old Password"
          type={showCode ? "text" : "password"}
          onChange={handleChange}
          value={formValues.oldPassword}
          placeholder="Old Password"
          variant="outlined"
          style={{ borderColor: isError ? "red" : "inherit", borderWidth: 1 }}
          validators={["required"]}
          errorMessages={["Old Password is required"]}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle code"
                  onClick={() => setShowCode(!showCode)}
                  onMouseDown={() => setShowCode(!showCode)}
                  edge="end"
                >
                  {showCode ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            ),
          }}
        />
        <br />
        <TextValidator
          margin="normal"
          required
          fullWidth
          name="newPassword"
          label="New Password"
          type={showCode2 ? "text" : "password"}
          onChange={handleChange}
          value={formValues.newPassword}
          placeholder="New Password"
          variant="outlined"
          validators={["required"]}
          errorMessages={["New Password is required"]}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle code"
                  onClick={() => setShowCode2(!showCode2)}
                  onMouseDown={() => setShowCode2(!showCode2)}
                  edge="end"
                >
                  {showCode2 ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            ),
          }}
        />
        <br />
        <Button
          type="submit"
          variant="contained"
          disabled={isLoading}
          fullWidth
        >
          Update Password
        </Button>
      </ValidatorForm>
    </div>
  );
};

