import { ArrowBackIosNew, Edit } from "@mui/icons-material";
import {
  Button,
  Container,
  Divider,
  Grid,
  IconButton,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import MUIRichTextEditor from "mui-rte";
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import CustomDialog from "../../../../components/dialogs/custom-dialog";
import CustomDialog2 from "../../../../components/dialogs/custom-dialog";
import CustomDialog3 from "../../../../components/dialogs/custom-dialog";
import CustomDialog4 from "../../../../components/dialogs/custom-dialog";

const PermSec = () => {
  const { permSecData } = useSelector((state) => state.permSec);
  const history = useHistory();

  const [open, setOpen] = React.useState(false);
  const [open2, setOpen2] = React.useState(false);
  const [open3, setOpen3] = React.useState(false);
  const [open4, setOpen4] = React.useState(false);

  return (
    <div>
      <Container>
        <CustomDialog />
        <CustomDialog2 />
        <CustomDialog3 />
        <Button
          onClick={() => history.goBack()}
          startIcon={<ArrowBackIosNew />}
        >
          Back
        </Button>
        <br />
        <br />
        <Grid container spacing={2}>
          <Grid item sm={6} md={5}>
            <Box display="flex" flexDirection="column">
              <img src={permSecData?.image} alt="" />
              <Button sx={{ mt: 1, textTransform: "none" }} variant="contained">
                Update Image
              </Button>
              <br />
              <Box
                paddingY={1}
                display={"flex"}
                flexDirection="row"
                justifyContent={"space-between"}
                alignItems="center"
              >
                <Typography>{permSecData?.name}</Typography>
                <Button
                  variant="contained"
                  sx={{ textTransform: "none" }}
                  startIcon={<Edit />}
                >
                  Update
                </Button>
              </Box>
              <Divider />
              <Box
                paddingY={1}
                display={"flex"}
                flexDirection="row"
                justifyContent={"space-between"}
                alignItems="center"
              >
                <Typography>{permSecData?.position}</Typography>
                <Button
                  variant="contained"
                  sx={{ textTransform: "none" }}
                  startIcon={<Edit />}
                >
                  Update
                </Button>
              </Box>
            </Box>
          </Grid>

          <Grid item sm={6} md={7}>
            <Box display="flex" flexDirection="column">
              <Box
                display="flex"
                flexDirection="row"
                justifyContent="space-between"
                alignItems="center"
              >
                <Typography variant="h6">BIOGRAPHY</Typography>
                <IconButton>
                  <Edit />
                </IconButton>
              </Box>
              <Divider />
              <MUIRichTextEditor
                readOnly
                inlineToolbar={false}
                toolbar={false}
                defaultValue={permSecData?.biography}
              />
            </Box>
          </Grid>
        </Grid>
        <br />
        <Divider />
        <br />
        <Box
          display="flex"
          flexDirection="row"
          justifyContent="space-between"
          alignItems="center"
        >
          <Typography variant="h6">WELCOME MESSAGE</Typography>
          <IconButton>
            <Edit />
          </IconButton>
        </Box>
        <Divider />
        <MUIRichTextEditor
          readOnly
          inlineToolbar={false}
          toolbar={false}
          defaultValue={permSecData?.message}
        />

        <br />
        <Divider />
        <br />
        <Box
          display="flex"
          flexDirection="row"
          justifyContent="space-between"
          alignItems="center"
        >
          <Typography variant="h6">
            MESSAGE SUMMARY (SHORT DESCRIPTION ON HOME PAGE)
          </Typography>
          <IconButton>
            <Edit />
          </IconButton>
        </Box>
        <Divider />
        <Typography>{permSecData?.summary}</Typography>
      </Container>
    </div>
  );
};

const UpdateImage = (props) => {
  let { image, setOpen } = props;

  return (
    <div>
      <ValidatorForm>
        <Grid container spacing={1} padding={1}>
          <Grid item xs={12} sm={6} md={7}>
            <TextValidator
              id="image"
              size="small"
              variant="outlined"
              value={formValues.image}
              name="image"
              type="file"
              fullWidth
              disabled={isLoading}
              accept=".png, .jpg, .jpeg"
              onChange={handleChange}
              helperText="Featured image"
            />
          </Grid>

          <Grid item xs={12} sm={6} md={5}>
            <div>
              <Avatar
                variant="rounded"
                alt="Passport"
                src={previewImage ? previewImage : img}
                className={classes.image}
              />
            </div>
          </Grid>
        </Grid>
      </ValidatorForm>
    </div>
  );
};

export default PermSec;
