import { createSlice } from "@reduxjs/toolkit";

export const rsphcmbSlice = createSlice({
  name: "rsphcmb",
  initialState: {
    whoWeAreData: null,
    visionData: null,
    missionData: null,
  },
  reducers: {
    setWhoWeAreData: (state, action) => {
      state.whoWeAreData = action.payload;
    },
    setVisionData: (state, action) => {
      state.visionData = action.payload;
    },
    setMissionData: (state, action) => {
      state.missionData = action.payload;
    },
  },
});

export const { setWhoWeAreData, setVisionData, setMissionData } =
  rsphcmbSlice.actions;

export default rsphcmbSlice.reducer;
