import { ArrowBackIosNew, Edit } from "@mui/icons-material";
import {
  Avatar,
  Backdrop,
  Button,
  CircularProgress,
  Container,
  Divider,
  Grid,
  IconButton,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import MUIRichTextEditor from "mui-rte";
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import CustomDialog from "../../../../components/dialogs/custom-dialog";
import CustomDialog2 from "../../../../components/dialogs/custom-dialog";
import CustomDialog3 from "../../../../components/dialogs/custom-dialog";
import CustomDialog4 from "../../../../components/dialogs/custom-dialog";
import { useSnackbar } from "notistack";
import {
  doc,
  getDownloadURL,
  uploadBytesResumable,
  ref,
  db,
  storage,
  updateDoc,
  deleteObject,
} from "../../../../../data/firebase";

const CircularProgressWithLabel = (props) => {
  return (
    <Box position="relative" display="inline-flex">
      <CircularProgress
        variant="determinate"
        {...props}
        size={90}
        thickness={3.0}
        style={{ color: "green" }}
      />
      <Box
        top={0}
        left={0}
        bottom={0}
        right={0}
        position="absolute"
        display="flex"
        alignItems="center"
        justifyContent="center"
      >
        <Typography
          variant="body1"
          component="div"
          style={{ color: "white", fontFamily: "roboto" }}
        >{`${Math.round(props.value)}%`}</Typography>
      </Box>
    </Box>
  );
};

const PermSec = () => {
  const { permSecData } = useSelector((state) => state.permSec);
  const history = useHistory();

  const [open, setOpen] = React.useState(false);
  const [open2, setOpen2] = React.useState(false);
  const [open3, setOpen3] = React.useState(false);
  const [open4, setOpen4] = React.useState(false);

  return (
    <div>
      <Container>
        <CustomDialog
          title="Update Picture"
          open={open}
          bodyComponent={
            <UpdateImage setOpen={setOpen} image={permSecData?.image} />
          }
        />
        <CustomDialog2 />
        <CustomDialog3 />
        <Button
          onClick={() => history.goBack()}
          startIcon={<ArrowBackIosNew />}
        >
          Back
        </Button>
        <br />
        <br />
        <Grid container spacing={2}>
          <Grid item sm={6} md={5}>
            <Box display="flex" flexDirection="column">
              <img src={permSecData?.image} alt="" />
              <Button sx={{ mt: 1, textTransform: "none" }} variant="contained">
                Update Image
              </Button>
              <br />
              <Box
                paddingY={1}
                display={"flex"}
                flexDirection="row"
                justifyContent={"space-between"}
                alignItems="center"
              >
                <Typography>{permSecData?.name}</Typography>
                <Button
                  variant="contained"
                  sx={{ textTransform: "none" }}
                  startIcon={<Edit />}
                >
                  Update
                </Button>
              </Box>
              <Divider />
              <Box
                paddingY={1}
                display={"flex"}
                flexDirection="row"
                justifyContent={"space-between"}
                alignItems="center"
              >
                <Typography>{permSecData?.position}</Typography>
                <Button
                  variant="contained"
                  sx={{ textTransform: "none" }}
                  startIcon={<Edit />}
                >
