import {
  Avatar,
  Backdrop,
  Button,
  CircularProgress,
  Grid,
  Typography,
  IconButton,
  Divider,
} from "@mui/material";
import React from "react";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import { makeStyles } from "@mui/styles";
import { useTheme } from "@mui/material";
import { useMediaQuery } from "@mui/material";
import { Box } from "@mui/system";
import {
  db,
  doc,
  ref,
  storage,
  uploadBytesResumable,
  updateDoc,
  getDownloadURL,
} from "../../../../../data/firebase";
import Edit from "@mui/icons-material/Edit";

import CustomDialogFN from "../../../../components/dialogs/custom-dialog";
import CustomDialogLN from "../../../../components/dialogs/custom-dialog";
import CustomDialogNUM from "../../../../components/dialogs/custom-dialog";

import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";

const useStyles = makeStyles((theme) => ({
  root: {},
  divRoot: {
    display: "flex",
    padding: theme.spacing(3),
    flexDirection: "column",
    alignItems: "center",
  },
  avatarContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    borderColor: "#3EB3CE",
    borderWidth: 3,
    borderStyle: "solid",
  },
  avatar: {
    backgroundColor: theme.palette.secondary.main,
  },
  buttonRow: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
  },
  button: {
    margin: theme.spacing(1),
  },
  progress: {
    color: "white",
    zIndex: 500,
    margin: "auto",
  },
}));

const CircularProgressWithLabel = (props) => {
  return (
    <Box position="relative" display="inline-flex">
      <CircularProgress
        variant="determinate"
        {...props}
        size={90}
        thickness={3.0}
        style={{ color: "green" }}
      />
      <Box
        top={0}
        left={0}
        bottom={0}
        right={0}
        position="absolute"
        display="flex"
        alignItems="center"
        justifyContent="center"
      >
        <Typography
          variant="body1"
          component="div"
          style={{ color: "white", fontFamily: "roboto" }}
        >{`${Math.round(props.value)}%`}</Typography>
      </Box>
    </Box>
  );
};

const Account = () => {
  const classes = useStyles();
  const { myData } = useSelector((state) => state.user);
  const [imageFile, setImageFile] = React.useState(null);
  const [imageUrl, setImageUrl] = React.useState("");
  const [isUploading, setIsUploading] = React.useState(false);
  const [isLoading, setLoading] = React.useState(false);
  const [progress, setProgress] = React.useState(0);
  const fileInputRef = React.useRef();
  const { enqueueSnackbar } = useSnackbar();

  const [openFN, setOpenFN] = React.useState(false);
  const [openLN, setOpenLN] = React.useState(false);
  const [openNum, setOpenNum] = React.useState(false);

  React.useEffect(() => {
    console.log("USER DATA::", myData);
  }, [myData]);

  const initials =
    myData?.firstname?.slice(0, 1).toUpperCase() +
    myData?.lastname?.slice(0, 1).toUpperCase();

  let imgSize;
  const theme = useTheme();
  const xsmall = useMediaQuery(theme.breakpoints.only("xs"));
  const small = useMediaQuery(theme.breakpoints.only("sm"));
  const medium = useMediaQuery(theme.breakpoints.only("md"));
  const others = useMediaQuery(theme.breakpoints.up("lg"));

  if (xsmall) {
    imgSize = 150;
  } else if (small) {
    imgSize = 174;
  } else if (medium) {
    imgSize = 200;
  } else if (others) {
    imgSize = 236;
  }

  const handleFile = (e) => {
    setImageFile(e.target.files[0]);
    setImageUrl(URL.createObjectURL(e.target.files[0]));
  };

  const performSubmit = (e) => {
    setIsUploading(true);
    const timeNow = new Date();
    //First upload image to firebase storage then save to firestore
    const storageRef = ref(
      storage,
      "users-" + myData?.id + "/" + timeNow.getTime()
    );
    const uploadTask = uploadBytesResumable(storageRef, imageFile);

    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const uprogress =
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        // console.log("Upload is " + progress + "% done");
        setProgress(uprogress);
      },
      (error) => {
        // Handle unsuccessful uploads
        setIsUploading(false);
        // console.log(error);
        enqueueSnackbar(`${error?.message}`, { variant: "error" });
      },
      () => {
        setIsUploading(false);
        setLoading(true);
        getDownloadURL(uploadTask.snapshot.ref).then(async (downloadURL) => {
          const mRef = doc(db, "users", "" + myData?.id);
          try {
            await updateDoc(mRef, {
              image: downloadURL,
            });
            setLoading(false);
            setImageUrl(null);
            setImageFile(null);
            enqueueSnackbar(`Image updated successfully`, {
              variant: "success",
            });
          } catch (error) {
            setLoading(false);
            enqueueSnackbar(
              `${error?.message || "Check your internet connection"}`,
              {
                variant: "error",
              }
            );
          }
        });
      }
    );
  };

  return (
    <div>
      <CustomDialogFN
        open={openFN}
        bodyComponent={
          <UpdateFN
            setOpen={setOpenFN}
            userId={myData?.id}
            firstname={myData?.firstname}
          />
        }
      />
      <Backdrop style={{ zIndex: 1200 }} open={isUploading || isLoading}>
        {isUploading ? <CircularProgressWithLabel value={progress} /> : <div />}
        {isLoading ? (
          <CircularProgress
            size={90}
            thickness={3.0}
            style={{ color: "white" }}
          />
        ) : (
          <div />
        )}
      </Backdrop>
      <Typography variant="h6" fontWeight="600" gutterBottom={true}>
        Personal Account Information
      </Typography>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          height: "70vh",
        }}
      >
        <Grid
          container
          spacing={4}
          justifyContent="space-between"
          alignItems={"center"}
        >
          <Grid item sm={6} md={6}>
            <div className={classes.divRoot}>
              <div
                className={classes.avatarContainer}
                style={{
                  width: imgSize + 3,
                  height: imgSize + 3,
                  borderRadius: (imgSize + 3) / 2,
                }}
              >
                <Avatar
                  src={
                    imageUrl
                      ? imageUrl
                      : myData?.image !== ""
                      ? myData?.image
                      : ""
                  }
                  style={{
                    width: imgSize - 12,
                    height: imgSize - 12,
                    borderRadius: (imgSize - 12) / 2,
                    fontSize: 64,
                  }}
                >
                  {myData?.image !== "" ? "" : initials}
                </Avatar>
              </div>
              <br />
              <input
                hidden={true}
                ref={fileInputRef}
                type="file"
                onChange={handleFile}
              />

              <div className={classes.buttonRow}>
                <Button
                  className={classes.button}
                  variant="contained"
                  color="secondary"
                  disableElevation={true}
                  sx={{ backgroundColor: "#00B0EF", textTransform: "none" }}
                  onClick={() => fileInputRef.current.click()}
                >
                  Change Profile
                </Button>

                <Button
                  hidden={imageFile ? false : true}
                  disabled={isLoading || isUploading}
                  className={classes.button}
                  variant="contained"
                  color="primary"
                  disableElevation={true}
                  onClick={performSubmit}
                  sx={{ textTransform: "none" }}
                >
                  Save
                </Button>
              </div>
            </div>
          </Grid>
          <Grid item sm={6} md={6} alignItems={"center"}>
            <Box
              paddingY={1}
              display="flex"
              flexDirection="row"
              justifyContent={"space-between"}
              alignItems="center"
            >
              <Box
                display="flex"
                flexDirection="row"
                justifyContent={"start"}
                alignItems="center"
              >
                <Typography>FIRSTNAME</Typography>
                <Typography variant="h6" pl={2}>
                  {myData?.firstname}
                </Typography>
              </Box>
              <IconButton>
                <Edit />
              </IconButton>
            </Box>
            <Divider />
            <Box
              paddingY={1}
              display="flex"
              flexDirection="row"
              justifyContent={"space-between"}
              alignItems="center"
            >
              <Box
                display="flex"
                flexDirection="row"
                justifyContent={"start"}
                alignItems="center"
              >
                <Typography>LASTNAME</Typography>
                <Typography variant="h6" pl={2}>
                  {myData?.lastname}
                </Typography>
              </Box>
              <IconButton>
                <Edit />
              </IconButton>
            </Box>
            <Divider />
            <Box
              paddingY={1}
              display="flex"
              flexDirection="row"
              justifyContent={"space-between"}
              alignItems="center"
            >
              <Box
                display="flex"
                flexDirection="row"
                justifyContent={"start"}
                alignItems="center"
              >
                <Typography>PHONE NUMBER</Typography>
                <Typography variant="h6" pl={2}>
                  {myData?.phone}
                </Typography>
              </Box>
              <IconButton>
                <Edit />
              </IconButton>
            </Box>
            <Divider />
            <Box
              paddingTop={5}
              display="flex"
              flexDirection="row"
              justifyContent={"stretch"}
              alignItems="center"
            >
              <Button
                variant="contained"
                fullWidth
                disableElevation={true}
                sx={{ textTransform: "none" }}
              >
                Update Password{" "}
              </Button>
            </Box>
          </Grid>
        </Grid>
      </div>
    </div>
  );
};

const UpdateFN = (props) => {
  let { firstname, setOpen, userId } = props;
  const [formValues, setFormValues] = React.useState({
    firstname: firstname,
  });
  const [isLoading, setLoading] = React.useState(false);

  const { enqueueSnackbar } = useSnackbar();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues((prevData) => ({ ...prevData, [name]: value }));
  };

  const updateData = async (e) => {
    setLoading(true);

    try {
      const mRef = doc(db, "users", "" + userId);
      await updateDoc(mRef, {
        firstname: formValues.firstname,
      });
      setOpen(false);
      setLoading(false);
      enqueueSnackbar(`First name updated successfully`, {
        variant: "success",
      });
    } catch (error) {
      setLoading(false);
      enqueueSnackbar(`${error?.message || "Check your internet connection"}`, {
        variant: "error",
      });
    }
  };

  return (
    <div>
      <Backdrop style={{ zIndex: 1200 }} open={isLoading}>
        {isLoading ? (
          <CircularProgress
            size={90}
            thickness={3.0}
            style={{ color: "white" }}
          />
        ) : (
          <div />
        )}
      </Backdrop>
      <ValidatorForm onSubmit={updateData}>
        <TextValidator
          label="First Name"
          size="small"
          variant="outlined"
          value={formValues.firstname}
          onChange={handleChange}
          name="firstname"
          fullWidth
          validators={["required"]}
          errorMessages={["First name is required"]}
        />
        <br />
        <Button
          type="submit"
          variant="contained"
          disabled={isLoading}
          fullWidth
        >
          Update Firstname
        </Button>
      </ValidatorForm>
    </div>
  );
};

